import React, { Component } from 'react';
import {connect} from 'react-redux';
import { Table, Button } from 'react-bootstrap';
class Login extends Component {
   constructor()
   {

       super();
       this.state=
        {
            username:"",
            password:"",
        
        }
        this.handleChange=this.handleChange.bind(this);
        this.handleLogin=this.handleLogin.bind(this);
   }
   handleChange(e)
   {
    this.setState(
        {
            [e.target.name]:e.target.value
        }
    )
           
       
   }
   handleLogin(e)
   {
       e.preventDefault();
      
         // this.props.credentials();
         const { loginUser, loginPassword } = this.state;
        this.props.credentials(loginUser, loginPassword);  
         this.props.login();
       
   }
    render() { 
        return ( 
            <Table responsive>
                <tr>
                    <th>Username</th>
                    <td><input type="text" name="username" ></input></td>
                </tr>
                <tr>
                    <th>Password</th>
                    <td><input type="password" name="password" ></input></td>
                </tr>
                <tr>
                    <td></td>
                    <td><Button variant="success" onClick={this.handleLogin}>Login</Button></td>
                </tr>
            </Table>
         );
    }
}
const mapDispatchToProps=(dispatch)=>{
    return{
        login:()=>dispatch({type:'Login',val:"true"}), //Action Creators
        credentials:()=> 'details'
       // credentials:()=>dispatch({type:'details',username:"username",password:"password"})
    }
}


 
export default connect(null,mapDispatchToProps) (Login);
