import React, { Component } from 'react';
import {connect} from 'react-redux';
import { Table, Button } from 'react-bootstrap';
class Login extends Component {
   constructor()
   {
       super();
       this.state=
        {
            username:"",
            password:"",
        
        }
        this.handleChange=this.handleChange.bind(this);
        this.handleLogin=this.handleLogin.bind(this);
   }
   handleChange(e)
   {
       this.setState(
           {
               [e.target.name]:e.target.value
           }
       )
   }
   handleLogin(e)
   {
       e.preventDefault();
       if(this.state.username==='admin' && this.state.password==='admin')
       {
           this.props.login();
       }
   }
    render() { 
        return ( 
            <Table responsive>
                <tr>
                    <th>Username</th>
                    <td><input type="text" name="username" onChange={this.handleChange}></input></td>
                </tr>
                <tr>
                    <th>Password</th>
                    <td><input type="password" name="password" onChange={this.handleChange}></input></td>
                </tr>
                <tr>
                    <td></td>
                    <td><Button variant="success" onClick={this.handleLogin}>Login</Button></td>
                </tr>
            </Table>
         );
    }
}

 
export default Login;
