import React, { Component } from 'react';
import Flippy, { FrontSide, BackSide } from 'react-flippy';
import { Container, Col, Row, Table } from 'react-bootstrap';
class CreateUser extends Component {
    constructor()
    {
        super();
        this.state={
            isFlipped:false
        };
        this.handleClick=this.handleClick.bind(this);
    }
    handleClick(e)
    {
        e.preventDefault();
        this.setState(prevState=>({isFlipped:!prevState.isFlipped}));
    }
    render() { 
        return (
           
                        <Flippy
                                isFlipped={this.state.isFlipped}
                                flipDirection="horizontal"
                                ref={(r) => this.flippy = r}
                                style={{ width: '400px', height: '400px',marginTop:"30px",marginLeft:"150px" }}
                        >
                        <FrontSide
                            style={{
                                    backgroundColor: '#41669d',
                                }}
                        >
                        <Container>
                            <Row>
                                <Col>
                                    <img src={ require('./newuser.png') } style={{height:"250px",width:"250px"}}/>
                                </Col> 
                            </Row>
                            <Row>
                                <Col> 
                                    <h2 style={{color:"purple"}}><i>Create User</i></h2>
                                </Col>
                            </Row>
                            <Row>
                                <Col>
                                    <button onClick={this.handleClick}>Click to flip</button>
                                </Col>
                            </Row>
                        </Container>
                        </FrontSide>
                        <BackSide
                            style={{ backgroundColor: '#175852'}}>
                       
                        <br></br>
                        Username: <input type="text" name="name"></input>
                        <button onClick={this.handleClick}>Back</button>
                        </BackSide>
                        </Flippy>
                

          );
    }
}
 
export default CreateUser;
