import React from 'react';
import { Container, Col, Row, Button,DropdownButton,Dropdown } from 'react-bootstrap';
class Header extends React.Component {
    constructor()
    {
        super();
        this.handle=this.handle.bind(this);
    }
    handle()
    {
        console.log('The link was clicked.');
    }
    render() { 
        return ( 
            <Container fluid="true">
            <Row style={{height:'7vh'}}>
            <Col xl={0} style={{backgroundColor: 'yellow'}}>logo</Col>
            <Col xs xl={9} lg md sm >PVG</Col>
            <Col xl={1} style={{backgroundColor:'pink'}}>
            <DropdownButton alignRight title="Admin" size="sm" style={{marginTop:"6px",marginLeftt:"19px"}}>
              <Dropdown.Item as="button" style={{backgroundColor:"aqua" }} onClick={this.handle}>Change Password</Dropdown.Item>
              <Dropdown.Item as="button">Logout</Dropdown.Item>
            </DropdownButton>
            </Col>
            </Row>
            </Container> 
        );
    }
}
 
export default Header;
