import React, { Component } from 'react';
import {connect} from 'react-redux';
import { Table, Button } from 'react-bootstrap';
class Login extends Component {
   constructor()
   {
       super();
        this.handleChange=this.handleChange.bind(this);
        this.handleLogin=this.handleLogin.bind(this);
   }
   handleChange(e)
   {
    let username=e.username.value;
    let password=e.password.value
           
       
   }
   handleLogin(e)
   {
       e.preventDefault();
       if(this.state.username==='admin' && this.state.password==='admin')
       {
           this.props.credentials();
           this.props.login();
       }
   }
    render() { 
        return ( 
            <Table responsive>
                <tr>
                    <th>Username</th>
                    <td><input type="text" name="username" ></input></td>
                </tr>
                <tr>
                    <th>Password</th>
                    <td><input type="password" name="password" ></input></td>
                </tr>
                <tr>
                    <td></td>
                    <td><Button variant="success" onClick={this.handleLogin}>Login</Button></td>
                </tr>
            </Table>
         );
    }
}
const mapDispatchToProps=(dispatch)=>{
    return{
        login:()=>dispatch({type:'Login',val:"true"}), //Action Creators
        credentials:()=>dispatch({type:'details',username:"username",password:"password"})
    }
}


 
export default connect(mapDispatchToProps) (Login);
