import React, { Component } from 'react';
import {connect} from 'react-redux';
import { Table, Button } from 'react-bootstrap';
let a="";
class Login extends Component {
   constructor()
   {

       super();
       this.state=
        {
            username:"initial",
            password:"",
        
        }
        this.handleChange=this.handleChange.bind(this);
        this.handleLogin=this.handleLogin.bind(this);
   }
   handleChange(e)
   {
    this.setState(
        {
            [e.target.name]:e.target.value
        }
    )
           
       
   }
   handleLogin(e)
   {
       e.preventDefault();
      
       a=this.state.username;
          this.props.credentials();
          
        
       /* const { username, password } = this.state;
         console.log("test");
         console.log("username :",username);
        this.props.credentials(username, password);*/  
         this.props.login();
       
   }
    render() { 
        return ( 
            <Table responsive bordered hover  style={{marginTop:"15vh"}}>
                <tbody>
                <tr>
                    <th style={{textAlign:"right"}}>Username :</th>
                    <td ><input type="text" name="username" onChange={this.handleChange}></input></td>
                </tr>
                <tr>
                    <th style={{textAlign:"right"}}>Password :</th>
                    <td><input type="password" name="password" onChange={this.handleChange} ></input></td>
                </tr>
                <tr>
                    
                    <td colSpan="2" style={{textAlign:"center"}}><Button variant="success" onClick={this.handleLogin}>Login</Button></td>
                </tr>
                </tbody>
            </Table>
         );
    }
}
//console.log("s",a)
//console.log("hi");
const mapDispatchToProps=(dispatch)=>{
    return{
        login:()=>dispatch({type:'Login',val:"true"}), //Action Creators
        //credentials:()=>'details'
       credentials:()=>dispatch({type:'details',username:a,password:"password"})
    }
}


 
export default connect(null,mapDispatchToProps) (Login);
